CREATE TABLE exam(exam_id int NOT NULL CONSTRAINT PK_exam_id PRIMARY KEY(exam_id),date date,time date,type varchar(0),ccontroller varchar(0),prepod int,group varchar(10),discipline int, CONSTRAINT FK_exam_prepods FOREIGN KEY (exam_id) REFERENCES prepods(prepod_id));
CREATE TABLE discipline(discipline_id int NOT NULL CONSTRAINT PK_discipline_id PRIMARY KEY(discipline_id),name varchar(0),number_of_lecture int,number_of_prectics int,number_of_labs int, CONSTRAINT FK_discipline_exam FOREIGN KEY (discipline_id) REFERENCES exam(exam_id));
CREATE TABLE para(para_id int NOT NULL CONSTRAINT PK_para_id PRIMARY KEY(para_id),prepod varchar(0),date date,time date,discipline int,group varchar(10),prepod_id_FK int CONSTRAINT FK_para_prepods FOREIGN KEY (prepod_id_FK) REFERENCES prepods(prepod_id), CONSTRAINT FK_para_discipline FOREIGN KEY (para_id) REFERENCES discipline(discipline_id));
CREATE TABLE prepods(prepod_id int NOT NULL CONSTRAINT PK_prepod_id PRIMARY KEY(prepod_id),name varchar(0),position varchar(0),degree varchar(0),phone_number varchar(0),birdthday date);
CREATE TABLE groups(num_groups varchar(10) NOT NULL CONSTRAINT PK_num_groups PRIMARY KEY(num_groups),student_count int,discipline_id_FK int CONSTRAINT FK_groups_discipline FOREIGN KEY (discipline_id_FK) REFERENCES discipline(discipline_id),exam_id_FK int CONSTRAINT FK_groups_exam FOREIGN KEY (exam_id_FK) REFERENCES exam(exam_id));
CREATE TABLE cafedraStaff(staff_id int NOT NULL CONSTRAINT PK_staff_id PRIMARY KEY(staff_id),name varchar(0),position varchar(0),adress varchar(0),birdthday date,phone_number varchar(0),exam_id_FK int CONSTRAINT FK_cafedraStaff_exam FOREIGN KEY (exam_id_FK) REFERENCES exam(exam_id));
CREATE TABLE students(student_id int NOT NULL CONSTRAINT PK_student_id PRIMARY KEY(student_id),name varchar(0),group varchar(10),birdthday date,phone_number varchar(0),num_groups_FK varchar(10) CONSTRAINT FK_students_groups FOREIGN KEY (num_groups_FK) REFERENCES groups(num_groups));
